package com.ufgov.zc.client.common.converter;import java.util.List;import java.util.Vector;import javax.swing.table.TableModel;import com.ufgov.zc.client.common.LangTransMeta;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.common.jj.model.JjPlan;import com.ufgov.zc.common.system.constants.JjElementConstants;public class JjToTableModelConverter {  public static TableModel convertJjPlanToTableModel(List jjPlanList) {    return convertJjPlanToTableModel(jjPlanList, false);  }  public static TableModel convertJjPlanToTableModel(List jjPlanList, boolean more) {    Vector names = new Vector();    Vector values = new Vector();    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_JJ_PLAN_CODE));    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_PROJECT_NAME));    if (more) {      names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_CO_NAME));      names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_DIRECTOR_CO_NAME));    }    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_ORG_MONEY));    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_CUR_MONEY));    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_SUM_BALANCE));    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_SUM_EFFECT_MONEY));    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_SUM_PAY_MONEY));    names.add(LangTransMeta.translate(JjElementConstants.JJ_TRANS_PROC_DATE));    for (int i = 0; i < jjPlanList.size(); i++) {      Vector eachRowData = new Vector();      JjPlan v = (JjPlan) jjPlanList.get(i);      eachRowData.add(v.getJjPlanCode());      eachRowData.add(v.getProjectName());      if (more) {        eachRowData.add(v.getCoName());        eachRowData.add(v.getDirectorCoName());      }      eachRowData.add(v.getOrgMoney());      eachRowData.add(v.getCurMoney());      eachRowData.add(v.getSumBalance());      eachRowData.add(v.getSumEffectMoney());      eachRowData.add(v.getSumPayMoney());      eachRowData.add(v.getProcDate());      values.add(eachRowData);    }    MyTableModel tableModel = new MyTableModel(values, names) {      public Class getColumnClass(int column) {        if ((column >= 0) && (column < getColumnCount()) && this.getRowCount() > 0) {          for (int row = 0; row < this.getRowCount(); row++) {            if (getValueAt(row, column) != null) {              return getValueAt(row, column).getClass();            }          }        }        return Object.class;      }      public boolean isCellEditable(int row, int colum) {        return false;      }    };    tableModel.setList(jjPlanList);    return tableModel;  }}