rowData.add(zcEbEvalReport.getProjName());    //    rowData.add(zcEbEvalReport.getPackCode());    rowData.add(zcEbEvalReport.getPackName());    //    rowData.add(zcEbEvalReport.getStatus());    rowData.add(format.format(zcEbEvalReport.getExecuteDate()));    /*rowData.add(zcEbEvalReport.getCreator());    rowData.add(zcEbEvalReport.getTsqkDescription());    rowData.add(zcEbEvalReport.getEvalDescription());    rowData.add(zcEbEvalReport.getReportAttachBlobid());    rowData.add(zcEbEvalReport.getSignReportAttachBlobid());    rowData.add(zcEbEvalReport.getRecordCode());    rowData.add(zcEbEvalReport.getReportCode());    rowData.add(zcEbEvalReport.getPackCode());*/    return rowData;  }}