  //第一列显示的是供应商的名称，最后一列显示的是总分列        if (colum == 0 || colum == this.getColumnCount() - 1) {          return false;        }        EvalPackProvider provider = (EvalPackProvider) list.get(row);        selectedBean = (ZcEbEvalItemResult) provider.getItemMap().get(itemList.get(colum - 1).getItemCode());        evalPanel.text1.setText(provider.getProviderName());        StringBuffer itemDescStr = new StringBuffer();        itemDescStr.append("指标名称：" + selectedBean.getItemName() + "\t\t\t");        if (selectedBean.getItemType().equals(EvalItemType.SCORE)) {          itemDescStr.append("标准分值：" + selectedBean.getStandardScore() + "\t\t\t\n");          if (selectedBean.getFormula() != null) {            itemDescStr.append(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_ZC_EB_FORMULA_ITEM_FORMULA) + ": "            + selectedBean.getFormula().trim() + "\n");          }        }        if (selectedBean.getDescription() != null) {          itemDescStr.append(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_ZC_EB_FORMULA_ITEM_DESCRIPTION) + ": "          + selectedBean.getDescription().trim() + "\n");        }        evalPanel.text2.setText(itemDescStr.toString());        if (itemList.get(colum - 1).getFormula() != null) {          return false;        }        return true;      }    };    tableModel.setList(list);    return tableModel;  }  @SuppressWarnings("unchecked")  protected Vector<Object> getValue(List list) {    Vector<Object> values = new Vector<Object>();    if (null != list) {      for (int i = 0; i < list.size(); i++) {        values.add(toRowData((EvalPackProvider) list.get(i)));      }    }    return values;  }  private Vector<Object> toRowData(EvalPackProvider provider) {    Vector<Object> rowData = new Vector<Object>();    rowData.add(provider.getProviderName());    if (itemType.equals(EvalItemType.SCORE)) {      for (int i = 0; i < itemList.size(); i++) {        ZcEbEvalItemResult item = (ZcEbEvalItemResult) provider.getItemMap().get(itemList.get(i).getItemCode());        rowData.add(item.getExpertEvalScore());      }      //汇总列      rowData.add(((ZcEbEvalItemResult) provider.getItemMap().get("SC")).getExpertEvalScore());    } else {      for (int i = 0; i < itemList.size(); i++) {        ZcEbEvalItemResult item = (ZcEbEvalItemResult) provider.getItemMap().get(itemList.get(i).getItemCode());        rowData.add(item.getComplianceEvalValue());      }      //汇总列      rowData.add(((ZcEbEvalItemResult) provider.getItemMap().get("CP")).getComplianceEvalValue());    }    return rowData;  }  protected Vector<String> getColumnName() {    Vector<String> names = new Vector<String>();    names.add("供应商");    for (int i = 0; i < itemList.size(); i++) {      if (itemType.equals(EvalItemType.SCORE)) {        names.add(itemList.get(i).getItemName() + "(" + itemList.get(i).getStandardScore() + ")");      } else {        names.add(itemList.get(i).getItemName());      }    }    names.add("总分");    return names;  }  public ZcEbEvalItemResult getSelectedBean() {    return selectedBean;  }  public void setSelectedBean(ZcEbEvalItemResult selectedBean) {    this.selectedBean = selectedBean;  }}