package com.ufgov.zc.client.common.converter.zc;import java.util.ArrayList;import java.util.List;import java.util.Vector;import javax.swing.table.DefaultTableModel;import javax.swing.table.TableModel;import com.ufgov.zc.client.common.LangTransMeta;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.client.component.table.BeanTableModel;import com.ufgov.zc.client.component.table.ColumnBeanPropertyPair;import com.ufgov.zc.client.datacache.AsValDataCache;import com.ufgov.zc.common.system.constants.ZcElementConstants;import com.ufgov.zc.common.zc.model.ZcEbGuarantee;public class ZcEbGuaranteeToTableModelConverter {  public static DefaultTableModel convertToTableModel(List guaranteeList) {    MyTableModel tableModel = null;    Vector names = new Vector();    Vector values = new Vector();    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PROJ_CODE));    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PROJ_NAME));    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PROVIDER_CODE));    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PROVIDER_NAME));    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_LINKMAN));    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PHONE));    names.add(LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_IS_PAY_GUARANTEE));    if (guaranteeList != null && guaranteeList.size() > 0) {      for (int i = 0; i < guaranteeList.size(); i++) {        Vector rowData = new Vector();        ZcEbGuarantee zcEbGuarantee = (ZcEbGuarantee) guaranteeList.get(i);        rowData.add(zcEbGuarantee.getProjCode());        rowData.add(zcEbGuarantee.getProjName());        rowData.add(zcEbGuarantee.getProviderCode());        rowData.add(zcEbGuarantee.getProviderName());        rowData.add(zcEbGuarantee.getLinkMan());        rowData.add(zcEbGuarantee.getPhone());        rowData.add(AsValDataCache.getName("VS_ZC_EB_GUARANTEE_STATUS", zcEbGuarantee.getIsPayGuarantee()));        values.add(rowData);      }    }    tableModel = new MyTableModel(values, names) {      public Class getColumnClass(int column) {        if ((column >= 0) && (column < getColumnCount()) && this.getRowCount() > 0) {          for (int row = 0; row < this.getRowCount(); row++) {            if (getValueAt(row, column) != null) {              return getValueAt(row, column).getClass();            }          }        }        return Object.class;      }      public boolean isCellEditable(int row, int colum) {        return false;      }    };    tableModel.setList(guaranteeList);    return tableModel;  }  public static TableModel convertGuaranteeDeToTableModel(List deList) {    BeanTableModel tm = new BeanTableModel() {      /* (non-Javadoc)       * @see com.ufgov.gk.client.component.table.BeanTableModel#isCellEditable(int, int)       */      @Override      public boolean isCellEditable(int row, int column) {        String identifier = this.getColumnIdentifier(column);        if (identifier.equals("IS_PAY_GUARANTEE") || identifier.equals("GUARANTEE_DESC")) {          return true;        }        return false;      }    };    tm.setOidFieldName("signupPackId");    tm.setDataBean(deList, createColumnBeanPropertyPairs());    return tm;  }  private static List<ColumnBeanPropertyPair> createColumnBeanPropertyPairs() {    List<ColumnBeanPropertyPair> pairList = new ArrayList<ColumnBeanPropertyPair>();    ColumnBeanPropertyPair pair = new ColumnBeanPropertyPair();    /*    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("PACK_CODE");    pair.setBeanPropertyName("packCode");    pairList.add(pair);    */    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("PACK_NAME");    pair.setBeanPropertyName("packName");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("PACK_DESC");    pair.setBeanPropertyName("packDesc");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("DEPOSITE");    pair.setBeanPropertyName("bidDeposit");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("IS_PAY_GUARANTEE");    pair.setBeanPropertyName("isPayGuarantee");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("GUARANTEE_DESC");    pair.setBeanPropertyName("guaranteeDesc");    pairList.add(pair);    return pairList;  }}