package com.ufgov.zc.client.common.converter.zc;import java.util.List;import java.util.Vector;import javax.swing.table.DefaultTableModel;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.common.zc.model.ZcEbOptions;public class ZcEbOptionsToTableModelConverter {  public static DefaultTableModel convertToTableModel(List protocolList) {    MyTableModel tableModel = null;    Vector<String> names = new Vector<String>();    Vector values = new Vector();    names.add("项目编号");    names.add("项目名称");    names.add("采购单位");    names.add("意见函名称");    names.add("生成时间");    names.add("答复时间");    if (protocolList != null && protocolList.size() > 0) {      for (int i = 0; i < protocolList.size(); i++) {        Vector rowData = new Vector();        ZcEbOptions zcEbOptions = (ZcEbOptions) protocolList.get(i);        rowData.add(zcEbOptions.getProjCode());        rowData.add(zcEbOptions.getProjName());        rowData.add(zcEbOptions.getCoName());        rowData.add(zcEbOptions.getProtFile());        rowData.add(zcEbOptions.getCreatTime());        rowData.add(zcEbOptions.getAnswerTime());        values.add(rowData);      }    }    tableModel = new MyTableModel(values, names) {      public Class getColumnClass(int column) {        if ((column >= 0) && (column < getColumnCount()) && this.getRowCount() > 0) {          for (int row = 0; row < this.getRowCount(); row++) {            if (getValueAt(row, column) != null) {              return getValueAt(row, column).getClass();            }          }        }        return Object.class;      }      public boolean isCellEditable(int row, int colum) {        return false;      }    };    tableModel.setList(protocolList);    return tableModel;  }}