package com.ufgov.zc.client.common.converter.zc;import java.util.List;import java.util.Vector;import javax.swing.table.DefaultTableModel;import com.ufgov.zc.client.common.LangTransMeta;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.client.component.ui.conditionitem.ConditionFieldConstants;import com.ufgov.zc.client.datacache.AsValDataCache;import com.ufgov.zc.common.system.constants.ZcElementConstants;import com.ufgov.zc.common.zc.model.ZcEbPresent;import com.ufgov.zc.common.zc.model.ZcEbProtocol;public class ZcEbPresentToTableModelConverter {  public static DefaultTableModel convertToTableModel(List presentList) {    MyTableModel tableModel = null;    Vector<String> names = new Vector<String>();    Vector values = new Vector();    names.add("模板名称");    names.add("项目编号");    names.add("项目类型");    names.add("项目名称");    names.add("采购单位");    if (presentList != null && presentList.size() > 0) {      for (int i = 0; i < presentList.size(); i++) {        Vector rowData = new Vector();        ZcEbPresent present = (ZcEbPresent) presentList.get(i);        rowData.add(present.getMoldName());        rowData.add(present.getProjCode());        rowData.add(present.getProjType());        rowData.add(present.getProjName());        rowData.add(present.getCgdanwei());        values.add(rowData);      }    }    tableModel = new MyTableModel(values, names) {      public Class getColumnClass(int column) {        if ((column >= 0) && (column < getColumnCount()) && this.getRowCount() > 0) {          for (int row = 0; row < this.getRowCount(); row++) {            if (getValueAt(row, column) != null) {              return getValueAt(row, column).getClass();            }          }        }        return Object.class;      }      public boolean isCellEditable(int row, int colum) {        return false;      }    };    tableModel.setList(presentList);    return tableModel;  }}