package com.ufgov.zc.client.common.converter.zc;import java.util.List;import java.util.Vector;import javax.swing.table.DefaultTableModel;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.client.datacache.AsValDataCache;import com.ufgov.zc.common.zc.model.ZcEbProCompletion;@SuppressWarnings("unchecked")public class ZcEbProCompletionToTableModelConverter {  public static DefaultTableModel convertToTableModel(List projectList) {    MyTableModel tableModel = null;    Vector<String> names = new Vector<String>();    Vector values = new Vector();    prepareColumnHeader(names);    if (projectList != null && projectList.size() > 0) {      for (int i = 0; i < projectList.size(); i++) {        Vector rowData = new Vector();        ZcEbProCompletion zcEbProj = (ZcEbProCompletion) projectList.get(i);        rowData.add(zcEbProj.getProCode());        rowData.add(zcEbProj.getProName());        rowData.add(zcEbProj.getCompletionCode());        rowData.add(zcEbProj.getEntrustCode());        rowData.add(AsValDataCache.getName("ZC_VS_COMPLETION_STATUS", zcEbProj.getChengbanStatus()));        rowData.add(zcEbProj.getReportDate());        values.add(rowData);      }    }    tableModel = new MyTableModel(values, names) {      private static final long serialVersionUID = 3123244681117334262L;      public Class getColumnClass(int column) {        if ((column >= 0) && (column < getColumnCount()) && this.getRowCount() > 0) {          for (int row = 0; row < this.getRowCount(); row++) {            if (getValueAt(row, column) != null) {              return getValueAt(row, column).getClass();            }          }        }        return Object.class;      }      public boolean isCellEditable(int row, int colum) {        return false;      }    };    tableModel.setList(projectList);    return tableModel;  }  private static void prepareColumnHeader(Vector<String> names) {    names.add("项目编号");    names.add("项目名称");    names.add("竣工单号");    names.add("批办单号");    names.add("竣工单状态");    names.add("报告时间");  }}