package com.ufgov.zc.client.component.zc.dataexchange.model;import java.io.FileInputStream;import java.io.FileOutputStream;import java.io.IOException;import java.io.ObjectInputStream;import java.io.ObjectOutputStream;public class ObjectSerialize {  public static void outPutSerialize(Object obj, String filePath) throws IOException, ClassNotFoundException {    FileOutputStream fos = new FileOutputStream(filePath);    ObjectOutputStream oos = new ObjectOutputStream(fos);    try {      oos.writeObject(obj);      oos.close();    } catch (IOException e) {      System.out.print("E1:" + e);    }  }  public static Object inPutSerialize(Object obj, String filePath) throws IOException, ClassNotFoundException {    FileInputStream fis = new FileInputStream(filePath);    ObjectInputStream ois = new ObjectInputStream(fis);    try {      obj = ois.readObject();      ois.close();      return obj;    } catch (IOException e) {      System.out.println("E2:" + e);      return null;    }  }}