onditionDto.setDattr1(fieldSn);    bulletinList = self.zcEbBulletinServiceDelegate.getZcEbBulletinList(elementConditionDto, requestMeta);    //    ZcEbBulletinXunJiaBidEditProjPanel editPanel = new ZcEbBulletinXunJiaBidEditProjPanel(self,    //      new ListCursor(bulletinList, 0), "0", self, fieldSn);    return null;  }  public String getBulletinType() {    return ZcEbBulletinConstants.TYPE_BULLETIN_XUN_JIA_BID;  }  public static void main(String[] args) throws Exception {    SwingUtilities.invokeLater(new Runnable() {      public void run() {        try {          UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());          UIManager.setLookAndFeel(new BlueLookAndFeel());        } catch (Exception e) {          e.printStackTrace();        }        ZcEbBulletinXunJiaProjBidListPanel bill = new ZcEbBulletinXunJiaProjBidListPanel("");        JFrame frame = new JFrame("frame");        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        frame.setSize(800, 600);        frame.setLocationRelativeTo(null);        frame.getContentPane().add(bill);        frame.setVisible(true);      }    });  }}