package com.ufgov.zc.client.zc.evaluation;import java.awt.BorderLayout;import java.awt.Dialog;import java.awt.Dimension;import java.util.List;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.component.GkBaseDialog;/** *  * @author wuwb *  */public class ZcEbEvaluationDialog extends GkBaseDialog {  /**   *    */  private static final long serialVersionUID = 1L;  private ZcEbEvaluationDialog self = this;  private ZcEbEvaluationEditPanel editPanel;  @SuppressWarnings("unchecked")  public ZcEbEvaluationDialog(ZcEbEvaluationListPanel listPanel, List beanList,  int editingRow, String tabStatus) {    super(listPanel.getParentWindow(),    Dialog.ModalityType.APPLICATION_MODAL);    editPanel = new ZcEbEvaluationEditPanel(this.self, new ListCursor(    beanList, editingRow), tabStatus, listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("评价信息");    editPanel.setPreferredSize(new Dimension(800, 500));    this.setPreferredSize(new Dimension(800, 500));    this.moveToScreenCenter();    this.setVisible(true);  }}