package com.ufgov.zc.client.zc.expertselection;import java.awt.BorderLayout;import java.awt.Dialog;import java.awt.Dimension;import java.awt.event.KeyAdapter;import java.awt.event.KeyEvent;import java.awt.event.WindowAdapter;import java.awt.event.WindowEvent;import java.util.List;import javax.swing.JDialog;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.component.GkBaseDialog;@SuppressWarnings("unchecked")public class ZcEmExpertSelectionDialog extends GkBaseDialog {  private static final long serialVersionUID = -3578583561581905765L;  private ZcEmExpertSelectionDialog self = this;  private ZcEmExpertSelectionListPanel listPanel;  private ZcEmExpertSelectionEditPanel editPanel;  public ZcEmExpertSelectionDialog(ZcEmExpertSelectionListPanel listPanel, List beanList, int editingRow,  String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    this.listPanel = listPanel;    editPanel = new ZcEmExpertSelectionEditPanel(this.self, new ListCursor(beanList, editingRow), tabStatus,    this.listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle(listPanel.getTitle());    this.setMaxSizeWindow();    this.setMinimumSize(new Dimension(800, 600));    setVisible(true);    setDefaultCloseOperation(JDialog.DISPOSE_ON_CLOSE);    this.addWindowListener(new WindowAdapter() {      public void windowClosing(WindowEvent e) {        if (editPanel.timer != null) {          editPanel.timer.cancel();        }        dispose();      }      public void windowDeactivated(WindowEvent e) {        if (editPanel.timer != null) {          editPanel.timer.cancel();        }        dispose();      }    });  }  }