package com.ufgov.zc.client.zc.flowconsole.businessflow;import javax.swing.JFrame;import javax.swing.SwingUtilities;import javax.swing.UIManager;import com.ufgov.smartclient.plaf.BlueLookAndFeel;import com.ufgov.zc.client.zc.flowconsole.MoudelFlowConsoleCanvas;public class InviteBidFlowConsoleCanvas extends MoudelFlowConsoleCanvas {  /**   * 邀请招标   */  private static final long serialVersionUID = -208835548739784133L;  public InviteBidFlowConsoleCanvas() {    super("invite_config.xml");  }  public static void main(String[] args) {    SwingUtilities.invokeLater(new Runnable() {      public void run() {        try {          UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());          UIManager.setLookAndFeel(new BlueLookAndFeel());        } catch (Exception e) {          e.printStackTrace();        }        InviteBidFlowConsoleCanvas bill = new InviteBidFlowConsoleCanvas();        JFrame frame = new JFrame("frame");        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        frame.setSize(800, 600);        frame.setLocationRelativeTo(null);        frame.getContentPane().add(bill);        frame.setVisible(true);      }    });  }}