package com.ufgov.zc.client.zc.flowconsole.events;import java.awt.event.ActionEvent;import com.ufgov.zc.client.zc.flowconsole.parts.Button;public class ButtonMouseEvent extends ActionEvent {  /**   *    */  private static final long serialVersionUID = 4567732237312902687L;  private String nodeId;  private Button button;  public static final String CLICK_MOUSE_COMMAND = "click";  public ButtonMouseEvent(Object source, int id, String command, String nodeId, Button button) {    super(source, id, command);    this.nodeId = nodeId;    this.button = button;  }  public String getNodeId() {    return nodeId;  }  public Button getButton() {    return button;  }}