package com.ufgov.zc.client.zc.formula;import java.awt.BorderLayout;import java.awt.Dialog;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;import com.ufgov.zc.common.zc.model.ZcEbFormula;public class ZcEbFormulaDialog extends GkBaseDialog {  private static final long serialVersionUID = 1L;  private ZcEbFormulaDialog self = this;  private FormulaSetMainPanel editPanel;  private ZcEbFormulaListPanel listPanel;  public ZcEbFormulaDialog(ZcEbFormulaListPanel listPanel, ZcEbFormula zcEbFormula) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    this.listPanel = listPanel;    editPanel = new FormulaSetMainPanel(zcEbFormula, listPanel);    editPanel.setParentWindow(this);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("评审指标集");    this.setSize(UIConstants.DIALOG_2_LEVEL_WIDTH, UIConstants.DIALOG_2_LEVEL_HEIGHT);    setMaxSizeWindow();    if (zcEbFormula.getFormulaCode() != null) {      editPanel.refreshData();    }    this.pack();    this.setVisible(true);  }  @Override  public void closeDialog() {    editPanel.getZcEbFormulaEditPanel().doExit();  }}