package com.ufgov.zc.client.zc.present;import java.awt.BorderLayout;import java.awt.Dialog;import java.util.List;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;public class ZcEbPresentDialog extends GkBaseDialog {  /**   *    */  private static final long serialVersionUID = 1L;  private ZcEbPresentListPanel listPanel;  private ZcEbPresentEditPanel editPanel;  private ZcEbPresentDialog self = this;  public ZcEbPresentDialog(ZcEbPresentListPanel listPanel, List beanList, int editingRow, String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    this.listPanel = listPanel;    this.editPanel = new ZcEbPresentEditPanel(this.self, new ListCursor(beanList, editingRow), tabStatus, listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("开标大会致辞");    this.setSize(UIConstants.DIALOG_0_LEVEL_WIDTH, UIConstants.DIALOG_0_LEVEL_HEIGHT);    this.moveToScreenCenter();    this.pack();    this.setMaxSizeWindow();    this.setVisible(true);  }}