package com.ufgov.zc.client.zc.sppc;import java.awt.BorderLayout;import java.awt.Dialog;import java.util.List;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;public class ZcBdSppcDialog extends GkBaseDialog {  private ZcBdSppcDialog self = this;  private ZcBdSppcEditPanel editPanel;  private ZcBdSppcListPanel listPanel;  public ZcBdSppcDialog(ZcBdSppcListPanel listPanel, List beanList, int editingRow, String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    this.listPanel = listPanel;    editPanel = new ZcBdSppcEditPanel(this.self, new ListCursor(beanList, editingRow), tabStatus, listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("商品批次");    this.setSize(UIConstants.DIALOG_4_LEVEL_WIDTH, UIConstants.DIALOG_4_LEVEL_HEIGHT);    this.setMaxSizeWindow();    this.moveToScreenCenter();    this.setVisible(true);  }  @Override  public void closeDialog() {    this.editPanel.doExit();  }}