package com.ufgov.zc.client.zc.workplan;import java.awt.BorderLayout;import java.awt.Dialog;import java.util.List;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;/** * 报名编辑界面 * @author Administrator * */public class ZcWorkPlanDialog extends GkBaseDialog {  private static final long serialVersionUID = 1L;  private ZcWorkPlanDialog self = this;  private ZcWorkPlanEditPanel editPanel;  public ZcWorkPlanDialog(ZcWorkPlanListPanel listPanel, List beanList, int editingRow, String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    editPanel = new ZcWorkPlanEditPanel(this.self, new ListCursor(beanList, editingRow), tabStatus, listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("工作计划管理");    this.setSize(UIConstants.DIALOG_0_LEVEL_WIDTH, UIConstants.DIALOG_0_LEVEL_HEIGHT);    this.moveToScreenCenter();    this.setVisible(true);  }  public void closeDialog() {    this.editPanel.doExit();  }}