package com.ufgov.zc.client.zc.workplan.dynamic;import java.awt.BorderLayout;import java.awt.Dialog;import java.util.List;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;/** * 动态编辑界面 * @author LEO */public class ZcWorkPlanDynamicDialog extends GkBaseDialog {  private static final long serialVersionUID = 1L;  private ZcWorkPlanDynamicDialog self = this;  private ZcWorkPlanDynamicEditPanel editPanel;  public ZcWorkPlanDynamicDialog(ZcWorkPlanDynamicListPanel listPanel, List beanList, int editingRow,  String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    editPanel = new ZcWorkPlanDynamicEditPanel(this.self, new ListCursor(beanList, editingRow), tabStatus,    listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("工作动态管理");    this.setSize(UIConstants.DIALOG_0_LEVEL_WIDTH, UIConstants.DIALOG_0_LEVEL_HEIGHT);    this.moveToScreenCenter();    this.setVisible(true);  }  public void closeDialog() {    this.editPanel.doExit();  }}