package com.ufgov.zc.client.zc.zcebentrust;import java.awt.BorderLayout;import java.awt.Dialog;import java.awt.Image;import java.util.ArrayList;import javax.swing.ImageIcon;import com.ufgov.zc.client.common.LangTransMeta;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;import com.ufgov.zc.common.system.constants.ZcElementConstants;/** * 终止暂停任务 * @author wuwba * */public class ZcEbCanCelEntrustDialog extends GkBaseDialog {  private ZcEbCanCelEntrustDialog self = this;  private ZcEbCancelEntrustEditPanel editPanel;  public ZcEbCanCelEntrustDialog(ZcEbCancelEntrustListPanel listPanel, ArrayList beanList, int editingRow, String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    editPanel = new ZcEbCancelEntrustEditPanel(ZcEbCanCelEntrustDialog.this, new ListCursor(beanList, editingRow), tabStatus, listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle(LangTransMeta.translate(ZcElementConstants.TITLE_FIELD_CANCEL_ENTRUST));    this.setSize(UIConstants.DIALOG_0_LEVEL_WIDTH, UIConstants.DIALOG_0_LEVEL_HEIGHT);    this.moveToScreenCenter();    this.pack();    this.setVisible(true);    this.setMaxSizeWindow();    Image image = new ImageIcon(this.getClass().getResource("/img/windowicon.jpg")).getImage();    this.setIconImage(image);  }}