package com.ufgov.zc.client.zc.zcebopenbidteam;import com.ufgov.zc.client.common.ListCursor;import com.ufgov.zc.client.common.UIConstants;import com.ufgov.zc.client.component.GkBaseDialog;import java.awt.BorderLayout;import java.awt.Dialog;import java.util.List;public class ZcEbOpenBidTeamDialog extends GkBaseDialog {  private static final long serialVersionUID = 1L;  private ZcEbOpenBidTeamDialog self = this;  private ZcEbOpenBidTeamEditPanel editPanel;  private ZcEbOpenBidTeamListPanel listPanel;  public ZcEbOpenBidTeamDialog(ZcEbOpenBidTeamListPanel listPanel, List beanList, int editingRow, String tabStatus) {    super(listPanel.getParentWindow(), Dialog.ModalityType.APPLICATION_MODAL);    this.listPanel = listPanel;    editPanel = new ZcEbOpenBidTeamEditPanel(this.self, new ListCursor(beanList, editingRow), tabStatus, listPanel);    setLayout(new BorderLayout());    add(editPanel);    this.setTitle("开标组管理");    this.setSize(UIConstants.DIALOG_0_LEVEL_WIDTH, UIConstants.DIALOG_0_LEVEL_HEIGHT);    this.moveToScreenCenter();    this.setVisible(true);  }  /* (non-Javadoc)   * @see com.ufgov.gk.client.component.GkBaseDialog#closeDialog()   */  @Override  public void closeDialog() {    this.editPanel.doExit();  }}