package com.ufgov.zc.client.zc.ztb.component;import com.ufgov.zc.client.zc.ztb.model.SmartTreeNode;import javax.swing.*;import javax.swing.tree.DefaultTreeSelectionModel;import java.awt.*;import java.awt.event.MouseAdapter;import java.awt.event.MouseEvent;public class CheckBoxSeletionTree extends JPanel {  private static final long serialVersionUID = 2116938467826570576L;  private JTree tree;  private SmartTreeNode treeNode;  private JPanel containerPanel;  public CheckBoxSeletionTree(SmartTreeNode treeNode, JPanel parentPanel) {    this.treeNode = treeNode;    this.containerPanel = parentPanel;    initComponents();    paintScreen();  }  public void paintScreen() {    setLayout(new BorderLayout());    add(new JScrollPane(tree), BorderLayout.CENTER);  }  public void initComponents() {    this.tree = new JTree(treeNode);    this.tree.expandRow(1);    this.tree.setRootVisible(true);    this.tree.setCellRenderer(new CheckBoxNodeRenderer());    this.tree.setCellEditor(new CheckBoxNodeEditor(tree));    this.tree.setEditable(true);    DefaultTreeSelectionModel defaultTreeSelectionModel = new DefaultTreeSelectionModel();    defaultTreeSelectionModel.setSelectionMode(DefaultTreeSelectionModel.SINGLE_TREE_SELECTION);    this.tree.setSelectionModel(defaultTreeSelectionModel);    this.tree.addMouseListener(new MouseAdapter() {      public void mouseClicked(MouseEvent e) {        JCheckBox cb = ((CheckBoxNodeRenderer) tree.getCellRenderer()).getLeafRenderer();        cb.setSelected(!cb.isSelected());      }    });  }  public JTree getTree() {    return tree;  }  public void setTree(JTree tree) {    this.tree = tree;  }  public SmartTreeNode getTreeNode() {    return treeNode;  }  public void setTreeNode(SmartTreeNode treeNode) {    this.treeNode = treeNode;  }  public JPanel getContainerPanel() {    return containerPanel;  }}