package com.ufgov.zc.client.zc.ztb.component;import javax.swing.tree.DefaultMutableTreeNode;import java.util.Map;public class JTreeNode extends DefaultMutableTreeNode {  public static final String CODE = "CODE";  public static final String NAME = "NAME";  private String code;  private String name;  private Map<String, Object> nodeAttribute;  private boolean isShowCode = false;  private boolean selected;  private boolean leaf;  public JTreeNode() {    this(null);  }  public JTreeNode(Object userObject) {    this(userObject, true, false);  }  public JTreeNode(Object userObject, boolean allowsChildren, boolean isSelected) {    super(userObject, allowsChildren);    if (userObject != null) {      this.code = ((JTreeNode) userObject).getCode();      this.name = ((JTreeNode) userObject).getName();    }  }  public String getCode() {    return code;  }  public void setCode(String code) {    this.code = code;  }  public String getName() {    return name;  }  public void setName(String name) {    this.name = name;  }  public String toString() {    if (code != null) {      return isShowCode ? name + "(" + code + ")" : name;    } else {      return name;    }  }  public void setShowCode(boolean isShowCode) {    this.isShowCode = isShowCode;  }  public boolean isShowCode() {    return isShowCode;  }  public Map<String, Object> getNodeAttribute() {    return nodeAttribute;  }  public void setNodeAttribute(Map<String, Object> nodeAttribute) {    this.nodeAttribute = nodeAttribute;  }  public void setSelected(boolean selected) {    this.selected = selected;  }  public boolean isSelected() {    return selected;  }  public boolean isLeaf() {    return leaf;  }  public void setLeaf(boolean leaf) {    this.leaf = leaf;  }}