package com.ufgov.zc.client.zc.ztb.component;import com.ufgov.zc.client.zc.ztb.util.GV;import com.ufgov.zc.client.zc.ztb.util.PubFunction;import javax.swing.*;public class ZTBButton extends JButton {  private static final long serialVersionUID = -1175646226971504743L;  public ZTBButton(String str) {    super();    String text = GV.getButtonText().get(str);    text = PubFunction.trim(text).equals("") ? str : text;    this.setText(text);    this.setIcon(GV.getImageIcon().get(str));    this.setToolTipText(text);  }}