package com.ufgov.zc.client.zc.ztb.dao;import com.ufgov.zc.client.zc.ztb.fileResumeBroken.authentication.Transfer;import java.util.HashMap;import java.util.Map;public class FormulaImportDao extends BaseDao {  public String importFormulaDesc(String projCode, String packCode) throws Exception {    Map<String, Object> para = new HashMap<String, Object>();    para.put("operationname", "searchformuladescbyprojpackcode");    para.put("PROJCODE", projCode);    para.put("PACKCODE", packCode);    para.put("URL", getServerURL());    Transfer transfer = new Transfer(para, "datasearchaction");    transfer.startTransfer();    if ("success".equals((String) transfer.getReturnMap().get("SEARCHRESULT"))) {      return (String) transfer.getReturnMap().get("FORMULADESC");    }    return "";  }}