package com.ufgov.zc.client.zc.ztb.dao;import com.thoughtworks.xstream.XStream;import com.thoughtworks.xstream.io.xml.DomDriver;import com.ufgov.zc.client.zc.ztb.model.BusinessProject;import com.ufgov.zc.client.zc.ztb.model.BusinessProjects;import com.ufgov.zc.client.zc.ztb.model.DBProperty;import com.ufgov.zc.client.zc.ztb.model.ProjectBag;import com.ufgov.zc.client.zc.ztb.table.entity.table.XmlTable;import com.ufgov.zc.client.zc.ztb.table.entity.table.XmlTableCell;import com.ufgov.zc.client.zc.ztb.table.entity.table.XmlTableColumn;import com.ufgov.zc.client.zc.ztb.table.entity.table.XmlTableRow;import com.ufgov.zc.client.zc.ztb.util.MyXppDriver;public class TbFileDao {  protected static XStream xstreamR;  protected static XStream xstreamW;  protected TbFileDao() {    xstreamR = new XStream(new DomDriver());    xstreamR.alias("BusinessProjects", BusinessProjects.class);    xstreamR.alias("BusinessProject", BusinessProject.class);    xstreamR.alias("ProjectBag", ProjectBag.class);    xstreamR.alias("XmlTable", XmlTable.class);    xstreamR.alias("XmlTableColumn", XmlTableColumn.class);    xstreamR.alias("XmlTableRow", XmlTableRow.class);    xstreamR.alias("XmlTableCell", XmlTableCell.class);    xstreamR.alias("DBProperty", DBProperty.class);    xstreamW = new XStream(new MyXppDriver());    xstreamW.alias("BusinessProjects", BusinessProjects.class);    xstreamW.alias("BusinessProject", BusinessProject.class);    xstreamW.alias("ProjectBag", ProjectBag.class);    xstreamW.alias("XmlTable", XmlTable.class);    xstreamW.alias("XmlTableColumn", XmlTableColumn.class);    xstreamW.alias("XmlTableRow", XmlTableRow.class);    xstreamW.alias("XmlTableCell", XmlTableCell.class);    xstreamW.alias("DBProperty", DBProperty.class);  }}