package com.ufgov.zc.client.zc.ztb.docautogenerate.operate;import freemarker.template.Configuration;import freemarker.template.Template;public class FreemarkerTemplateConfiguration {  private String templateName;  public String getTemplateName() {    return templateName;  }  public void setTemplateName(String templateName) {    this.templateName = templateName;  }  public FreemarkerTemplateConfiguration(String templateName) {    this.templateName = templateName;  }  public Template getTemplate() throws Exception {    Configuration configuration = new Configuration();    configuration.setDefaultEncoding("utf-8");    configuration.setClassForTemplateLoading(this.getClass(), "/com/ufgov/zc/client/zc/ztb/docautogenerate/template");    Template template = configuration.getTemplate(getTemplateName());    return template;  }}