package com.ufgov.zc.client.zc.ztb.fileResumeBroken.authentication;import com.ufgov.zc.client.zc.ztb.fileResumeBroken.fileOperate.MD5Util;import java.io.File;import java.util.HashMap;import java.util.Map;public class FileRelateInformation {  private Map fileRelateInformationMap = new HashMap();  public Map getFileRelateInformationMap() {    return fileRelateInformationMap;  }  public void setFileRelateInformationMap(Map fileRelateInformationMap) {    this.fileRelateInformationMap = fileRelateInformationMap;  }  public FileRelateInformation(Map parameterMap) {    setFileRelateInformationMap(parameterMap);    genMd5Code();  }  public void genMd5Code() {    long begin = System.currentTimeMillis();    File big = new File((String) fileRelateInformationMap.get("FILEPATH"));    String md5 = null;    try {      md5 = (new MD5Util()).getBigFileMD5(big);    } catch (Exception e) {      e.printStackTrace();    }    System.out.println("md5File:" + big.getAbsolutePath());    fileRelateInformationMap.put("MD5CODE", md5);    long end = System.currentTimeMillis();    System.out.println("MD5CODE:" + md5 + " time:" + ((end - begin) / 1000) + "s");  }}