package com.ufgov.zc.client.zc.ztb.model;import java.io.Serializable;import java.math.BigDecimal;import java.util.ArrayList;import java.util.List;import com.ufgov.zc.client.zc.ztb.model.face.ISmartUserObject;import com.ufgov.zc.client.zc.ztb.model.face.ITemplate;public class BusinessPack implements ISmartUserObject, Cloneable,Serializable {  private String id;  private String no;  private String name;  private String packDesc;  private String type;  private String item;  private BigDecimal bidSum;  private List<Object> ztbFile;  /**   * 是否已提交保证金   */  private String isSubmitBaoZhengJin;  /**   * 是否提交标书   */  private String isSubmitDidDoc;  private int ordIndex;  private String dir;  public Object clone() throws CloneNotSupportedException {    BusinessPack businessPack = (BusinessPack) super.clone();    List<Object> newZtbFileList = null;    if (getZtbFile() != null && getZtbFile().size() > 0) {      newZtbFileList = new ArrayList<Object>();      for (Object o : getZtbFile()) {        if (o instanceof BusinessPack) {          BusinessPack pp = (BusinessPack) o;          newZtbFileList.add((ITemplate) pp.clone());        } else if (o instanceof PackDetails) {          PackDetails pd = (PackDetails) o;          newZtbFileList.add((ITemplate) pd.clone());        } else {          throw new CloneNotSupportedException();        }      }    } else if (ztbFile == null) {      newZtbFileList = null;    } else if (ztbFile.size() == 0) {      newZtbFileList = new ArrayList<Object>();    }    businessPack.setZtbFile(newZtbFileList);    return businessPack;  }  public String getId() {    return id;  }  public void setId(String id) {    this.id = id;  }  public String getNo() {    return no;  }  public void setNo(String no) {    this.no = no;  }  public String getName() {    if (name == null) {      name = "";    }    return name;  }  public void setName(String name) {    this.name = name;  }  public String getPackDesc() {    return packDesc;  }  public void setPackDesc(String packDesc) {    this.packDesc = packDesc;  }  public String getType() {    return type;  }  public void setType(String type) {    this.type = type;  }  public String getItem() {    return item;  }  public void setItem(String item) {    this.item = item;  }  public BigDecimal getBidSum() {    return bidSum;  }  public void setBidSum(BigDecimal bidSum) {    this.bidSum = bidSum;  }  public List<Object> getZtbFile() {    return ztbFile;  }  public void setZtbFile(List<Object> ztbFile) {    this.ztbFile = ztbFile;  }  public int getOrdIndex() {    return ordIndex;  }  public void setOrdIndex(int ordIndex) {    this.ordIndex = ordIndex;  }  public String getDir() {    return dir;  }  public void setDir(String dir) {    this.dir = dir;  }  public String toString() {    return this.name;  }  public String getIsSubmitBaoZhengJin() {    return isSubmitBaoZhengJin;  }  public void setIsSubmitBaoZhengJin(String isSubmitBaoZhengJin) {    this.isSubmitBaoZhengJin = isSubmitBaoZhengJin;  }  public String getIsSubmitDidDoc() {    return isSubmitDidDoc;  }  public void setIsSubmitDidDoc(String isSubmitDidDoc) {    this.isSubmitDidDoc = isSubmitDidDoc;  }}