package com.ufgov.zc.client.zc.ztb.model;import java.io.Serializable;/** * 响应点model * * @author LEO */public class ResponsePoint implements Serializable {  private static int RP_COUNT = 0;  /**   * 响应点父节点id   */  private ResponsePoint parentPoint;  /**   * 响应点名称   */  private String pointName;  /**   * 响应点ID   */  private String pointID;  /**   * 响应点注释   */  private String pointMemo;  /**   * 响应点指向的文件位置，为相对路径   */  private String pointTargerPath;  /**   * 响应点内容   */  private String pointTargerContent;  /**   * 响应点书签   */  private String pointTargerBookMark;  /**   * 响应点类型：符合性、评分性   */  private String pointType;  private String pointDescription;  public ResponsePoint() {    RP_COUNT++;  }  public ResponsePoint getParentPoint() {    return parentPoint;  }  public void setParentPoint(ResponsePoint parentPoint) {    this.parentPoint = parentPoint;  }  public String getPointName() {    return pointName;  }  public void setPointName(String pointName) {    this.pointName = pointName;  }  public String getPointID() {    return pointID;  }  public void setPointID(String pointID) {    this.pointID = pointID;  }  public String getPointMemo() {    return pointMemo;  }  public void setPointMemo(String pointMemo) {    this.pointMemo = pointMemo;  }  public String getPointTargerPath() {    return pointTargerPath;  }  public void setPointTargerPath(String pointTargerPath) {    this.pointTargerPath = pointTargerPath;  }  public String getPointTargerContent() {    return pointTargerContent;  }  public void setPointTargerContent(String pointTargerContent) {    this.pointTargerContent = pointTargerContent;  }  public String getPointType() {    return pointType;  }  public void setPointType(String pointType) {    this.pointType = pointType;  }  public String getPointDescription() {    return pointDescription;  }  public void setPointDescription(String pointDescription) {    this.pointDescription = pointDescription;  }  public String getPointTargerBookMark() {    return pointTargerBookMark;  }  public void setPointTargerBookMark(String pointTargerBookMark) {    this.pointTargerBookMark = pointTargerBookMark;  }  public static int getRP_COUNT() {    return RP_COUNT;  }  public static void setRP_COUNT(int rPCOUNT) {    RP_COUNT = rPCOUNT;  }}