package com.ufgov.zc.client.zc.ztb.model.face;public abstract class ASmartUserObject implements ISmartUserObject {  private String smartUserObjectCode = "";  private String smartUserObjectName = "";  private String smartUserObjectType = "";  private String isDisplayForBoth = "BOTH";  /**   * 获得节点代码   *   * @return   */  public String getSmartUserObjectCode() {    return this.smartUserObjectCode;  }  /**   * 获得节点名称   *   * @return   */  public String getSmartUserObjectName() {    return this.smartUserObjectName;  }  /**   * 获得节点类型   *   * @return   */  public String getSmartUserObjectType() {    return this.smartUserObjectType;  }  /**   * 设置是否在投标和招标文件中都显示   *   * @param value值为TB时表示只在招标文件制作器中显示， value值为ZB时，表示只在招标文件中显示，   *                                  <p/>   *                                  value值为BOTH时，表示两种情况下都显示；   * @return   */  public void setIsDisplayForBoth(String value) {    this.isDisplayForBoth = value;  }  public String getIsDisplayForBoth() {    return this.isDisplayForBoth;  }}