package com.ufgov.zc.client.zc.ztb.service;import com.ufgov.zc.client.zc.ztb.component.MainPanel;import com.ufgov.zc.client.zc.ztb.dao.AsFileDao;import com.ufgov.zc.client.zc.ztb.util.PubFunction;import java.io.File;import java.util.ArrayList;import java.util.List;import java.util.Map;public class BagDetailsService {  private AsFileDao asFileDao = AsFileDao.getInstance();  public List<Map<String, String>> findDetailsFilesList(String proNo, String bagNo) throws Exception {    return asFileDao.findDetailsFilesList(proNo, bagNo);  }  public void updateBagDetails(final List<String> checkedCodes, final String combineFilePath, final MainPanel mainPanel) throws Exception {    List<String> fListPath = new ArrayList<String>();    for (String fileId : checkedCodes) {      File f = asFileDao.readFile(fileId);      fListPath.add(f.getAbsolutePath());    }    mainPanel.getWordPane().combineMsWord(fListPath);    for (String fPath : fListPath) {      PubFunction.deleteFile(fPath);    }  }}