package com.ufgov.zc.client.zc.ztb.service;import java.io.IOException;import java.util.HashMap;import java.util.Map;import com.ufgov.zc.client.zc.ztb.dao.SystemPropertyDao;import com.ufgov.zc.client.zc.ztb.fileResumeBroken.authentication.Transfer;import com.ufgov.zc.client.zc.ztb.model.DBProperty;public class SystemConfigService {  private SystemPropertyDao dbPropertyDao = SystemPropertyDao.getInstance();  public DBProperty readDBProperty() throws Exception {    return dbPropertyDao.readDBProperty();  }  public void saveOrUpdateDBProperty(DBProperty dbProperty) throws IOException {    dbPropertyDao.createXMLFile(dbProperty);  }  public boolean toDeleteOldConfig() {    return dbPropertyDao.toDeleteOldConfigFile();  }  public DBProperty readDBPropertyOnly() throws Exception {    return dbPropertyDao.readDBPropertyOnly();  }  public Map<String, String> toTestLink(Map<String, String> paras) {    Map<String, String> para = new HashMap<String, String>();    para.put("operationname", "totestlink");    para.put("URL", paras.get("URL"));    Transfer transfer = new Transfer(para, "datasearchaction");    transfer.startTransfer();    return transfer.getReturnMap();  }}