package com.ufgov.zc.client.zc.ztb.table.tablecomponent;import javax.swing.*;import javax.swing.JTable;import javax.swing.table.JTableHeader;import javax.swing.table.TableCellRenderer;import javax.swing.table.TableColumnModel;import java.awt.*;import java.awt.event.ItemListener;import java.awt.event.MouseEvent;import java.awt.event.MouseListener;public class JTableCheckBoxHeader extends JCheckBox implements TableCellRenderer, MouseListener {  protected JTableCheckBoxHeader rendererComponent;  protected JPanel rendererComponent2 = new JPanel();  protected int column;  protected boolean mousePressed = false;  public JTableCheckBoxHeader(ItemListener itemListener) {    rendererComponent = this;    rendererComponent.addItemListener(itemListener);    rendererComponent2.setLayout(new BorderLayout());    rendererComponent2.add(rendererComponent, BorderLayout.CENTER);    rendererComponent2.setBorder(UIManager.getBorder("TableHeader.cellBorder"));  }  public Component getTableCellRendererComponent(JTable table, Object value, boolean isSelected, boolean hasFocus, int row, int column) {    if (table != null) {      JTableHeader header = table.getTableHeader();      if (header != null) {        rendererComponent.setForeground(header.getForeground());        rendererComponent.setBackground(header.getBackground());        rendererComponent.setFont(header.getFont());        header.addMouseListener(rendererComponent);      }    }    setColumn(column);    rendererComponent.setText("");    setHorizontalAlignment(JLabel.CENTER);    rendererComponent.setBorder(UIManager.getBorder("TableHeader.cellBorder"));    return rendererComponent2;  }  protected void setColumn(int column) {    this.column = column;  }  public int getColumn() {    return column;  }  protected void handleClickEvent(MouseEvent e) {    if (mousePressed) {      mousePressed = false;      JTableHeader header = (JTableHeader) (e.getSource());      JTable tableView = header.getTable();      TableColumnModel columnModel = tableView.getColumnModel();      int viewColumn = columnModel.getColumnIndexAtX(e.getX());      int column = tableView.convertColumnIndexToModel(viewColumn);      if (viewColumn == this.column && e.getClickCount() == 1 && column != -1) {        doClick();      }    }  }  public void mouseClicked(MouseEvent e) {    handleClickEvent(e);    ((JTableHeader) e.getSource()).repaint();  }  public void mousePressed(MouseEvent e) {    mousePressed = true;  }  public void mouseReleased(MouseEvent e) {  }  public void mouseEntered(MouseEvent e) {  }  public void mouseExited(MouseEvent e) {  }}