package com.ufgov.zc.client.zc.ztb.table.tablecomponent;import com.ufgov.zc.client.zc.ztb.table.entity.GridColumn;public class JTableColumn extends javax.swing.table.TableColumn {  private int defaultWidth;  private int sliderWidth = 0;  private GridColumn gridColumn;  public void setDefaultWidth(int defaultWidth) {    this.defaultWidth = defaultWidth;  }  public int getDefaultWidth() {    return defaultWidth;  }  public void setSliderWidth(int sliderWidth) {    this.sliderWidth = sliderWidth;  }  public int getSliderWidth() {    return sliderWidth;  }  public void setGridColumn(GridColumn gridColumn) {    this.gridColumn = gridColumn;  }  public GridColumn getGridColumn() {    return gridColumn;  }  public boolean isVisible() {    if (getMinWidth() == 0 && getMaxWidth() == 0) {      return false;    }    return true;  }}