package com.ufgov.zc.client.zc.ztb.table.tablecomponent;import javax.swing.event.TableColumnModelEvent;import javax.swing.event.TableColumnModelListener;import javax.swing.table.TableColumnModel;public class JTableHeader extends javax.swing.table.JTableHeader {  public JTableHeader() {  }  public JTableHeader(TableColumnModel cm) {    super(cm);  }  public void columnMoved(TableColumnModelEvent e) {    if (e.getFromIndex() == 0) {      ((TableColumnModel) e.getSource()).removeColumnModelListener((TableColumnModelListener) e);      return;    }    super.columnMoved(e);  }}