package com.ufgov.zc.client.zc.ztb.table.tablecomponent.checkbox;import com.ufgov.zc.client.zc.ztb.table.tablecomponent.JTableModel;import javax.swing.*;import java.awt.*;public class JTableCheckBoxEditor extends DefaultCellEditor {  /**   *   */  private static final long serialVersionUID = -4824814760658881743L;  public final static String IS_SELECT_STAT = "Y";  public final static String IS_NOT_SELECT_STAT = "N";  private JCheckBox jCheckBox;  private com.ufgov.zc.client.zc.ztb.table.tablecomponent.JTable jTable;  private int row;  private int column;  public JTableCheckBoxEditor(JCheckBox checkBox) {    super(checkBox);    jCheckBox = checkBox;    jCheckBox.setOpaque(false);    jCheckBox.setHorizontalAlignment(JLabel.CENTER);  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row, int column) {    this.jTable = (com.ufgov.zc.client.zc.ztb.table.tablecomponent.JTable) table;    this.row = jTable.convertRowIndexToModel(row);    this.column = column;    ((JTableModel) jTable.getModel()).updateRow(this.row, this.column, getValue(value));    return jCheckBox;  }  public Object getCellEditorValue() {    return new Boolean(jCheckBox.isSelected());  }  private String getValue(Object value) {    return IS_SELECT_STAT.equals(value) ? "N" : "Y";  }}