package com.ufgov.zc.client.zc.ztb.table.tablecomponent.combobox;import javax.swing.*;import javax.swing.plaf.basic.BasicComboBoxRenderer;import java.awt.*;public class JTableComboBoxRenderer extends BasicComboBoxRenderer {  public JTableComboBoxRenderer() {  }  public Component getListCellRendererComponent(JList list, Object value, int index, boolean isSelected, boolean cellHasFocus) {    super.getListCellRendererComponent(list, value, index, isSelected, cellHasFocus);    setHorizontalAlignment(SwingConstants.LEFT);    return this;  }}