package com.ufgov.zc.client.zc.ztb.table.tablecomponent.numberfield;import com.ufgov.zc.client.zc.ztb.table.entity.GridColumn;import com.ufgov.zc.client.zc.ztb.table.tablecomponent.textfield.JTableTextFieldEditor;import javax.swing.*;import java.awt.*;public class JTableNumberFieldEditor extends JTableTextFieldEditor {  public JTableNumberFieldEditor(JTextField textField) {    super(textField);  }  public JTableNumberFieldEditor(JTextField textField, GridColumn gridColumn) {    super(textField, gridColumn);  }  private static final long serialVersionUID = -3617921387650823962L;  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row, int column) {    if (value == null) {      value = "";    }    value = value.toString().replaceAll(",", "");    super.getTableCellEditorComponent(table, value, isSelected, row, column);    this.table = table;    this.row = table.convertRowIndexToModel(row);    this.column = table.convertColumnIndexToModel(column);    return editorComponent;  }}